double map_invloglog(double arg, map_arg_tp typ, void *param)
{
	/*
	 * the inverse loglog function
	 */
	switch (typ) {
	case MAP_FORWARD:
		/*
		 * extern = func(local) 
		 */
		return exp(-exp(-arg));
	case MAP_BACKWARD:
		/*
		 * local = func(extern) 
		 */
		return -log(-log(arg));
	case MAP_DFORWARD:
		/*
		 * d_extern / d_local 
		 */
		return exp(-arg - exp(-arg));
	case MAP_INCREASING:
		/*
		 * return 1.0 if montone increasing and 0.0 otherwise 
		 */
		return 1.0;
	default:
		abort();
	}
	abort();
	return 0.0;
}
double map_invcauchit(double arg, map_arg_tp typ, void *param)
{
	/*
	 * the inverse cauchit function
	 */
	switch (typ) {
	case MAP_FORWARD:
		/*
		 * extern = func(local) 
		 */
		return M_1_PI * atan(arg) + 0.5;
	case MAP_BACKWARD:
		/*
		 * local = func(extern) 
		 */
		return tan(M_PI * (arg - 0.5));
	case MAP_DFORWARD:
		/*
		 * d_extern / d_local 
		 */
		return M_1_PI / (SQR(arg) + 1.0);
	case MAP_INCREASING:
		/*
		 * return 1.0 if montone increasing and 0.0 otherwise 
		 */
		return 1.0;
	default:
		abort();
	}
	abort();
	return 0.0;
}
double map_invcloglog(double arg, map_arg_tp typ, void *param)
{
	/*
	 * the inverse cloglog function
	 */
	switch (typ) {
	case MAP_FORWARD:
		/*
		 * extern = func(local) 
		 */
		return 1.0 - exp(-exp(arg));
	case MAP_BACKWARD:
		/*
		 * local = func(extern) 
		 */
		return log(-log(1.0 - arg));
	case MAP_DFORWARD:
		/*
		 * d_extern / d_local 
		 */
		return exp(arg - exp(arg));
	case MAP_INCREASING:
		/*
		 * return 1.0 if montone increasing and 0.0 otherwise 
		 */
		return 1.0;
	default:
		abort();
	}
	abort();
	return 0.0;
}
double map_beta(double x, map_arg_tp typ, void *param)
{
	/*
	 * the map for the beta parameter, which can have a lower and upper range as well. If range.low=range.high, then its interpreted as range.low = -INF and
	 * range.high = INF, ie the mapping is the identity. If range.high = INF and range.low != INF, then the mapping is range.low + exp(...).
	 */

	double *range = (double *) param;

	if (param == NULL || ISEQUAL(range[0], range[1])) {
		return map_identity(x, typ, param);
	}

	if (ISINF(range[1]) && !ISINF(range[0])) {
		switch (typ) {
		case MAP_FORWARD:
			/*
			 * extern = func(local) 
			 */
			return range[0] + exp(x);
		case MAP_BACKWARD:
			/*
			 * local = func(extern) 
			 */
			return log(x - range[0]);
		case MAP_DFORWARD:
			/*
			 * d_extern / d_local 
			 */
			return exp(x);
		case MAP_INCREASING:
			/*
			 * return 1.0 if montone increasing and 0.0 otherwise 
			 */
			return 1.0;
		default:
			abort();
		}
	} else if (ISINF(range[0]) && !ISINF(range[1])) {
		FIXME("the case: ISINF(range[0]) && !ISINF(range[1]), is not yet implemented.");
		exit(EXIT_FAILURE);
	} else {
		/*
		 * Then the mapping is
		 * 
		 * range[0] + exp(x)/(1 + exp(x)) * (range[1] - range[0]) 
		 */

		double d = range[1] - range[0], xx;

		switch (typ) {
		case MAP_FORWARD:
			return range[0] + d * exp(x) / (1.0 + exp(x));
		case MAP_BACKWARD:
			xx = (x - range[0]) / d;
			return log(xx / (1.0 - xx));
		case MAP_DFORWARD:
			return d * exp(x) / SQR(1 + exp(x));
		case MAP_INCREASING:
			return 1.0;
		default:
			GMRFLib_ASSERT(0 == 1, GMRFLib_ESNH);
		}
	}
	return 0.0;
}
