double map_invlogit(double x, map_arg_tp typ, void *param)
{
	/*
	 * extern = exp(local) / (1 + exp(local)) 
	 */
	switch (typ) {
	case MAP_FORWARD:
		return exp(x) / (1.0 + exp(x));
	case MAP_BACKWARD:
		return log(x / (1.0 - x));
	case MAP_DFORWARD:
		return exp(x) / SQR(1.0 + exp(x));
	case MAP_INCREASING:
		return 1.0;
	default:
		GMRFLib_ASSERT(0 == 1, GMRFLib_ESNH);
	}
	return 0.0;
}
