double link_logoffset(double x, map_arg_tp typ, void *param, double *cov)
{
	/*
	 * the link-functions calls the inverse map-function 
	 */
	Link_param_tp *p;
	double beta, off, sign;

	if (!cov) {
		char *msg;
		GMRFLib_sprintf(&msg, "You need to pass the covariates to the link.model[logoffset] in the inla()-argument 'link.covariates'");
		inla_error_general(msg);
		exit(1);
	}
	if (cov[0] < 0.0) {
		char *msg;
		GMRFLib_sprintf(&msg, "The covariates to link.model[logoffset] must be all >= 0.0. Yours is [%g].", cov[0]);
		inla_error_general(msg);
		exit(1);
	}

	p = (Link_param_tp *) param;
	beta = exp(p->beta_intern[GMRFLib_thread_id][0]);
	off = beta * cov[0];
	sign = (p->variant == 0 ? 1.0 : -1.0);

	switch (typ) {
	case MAP_FORWARD:
		return off + sign * exp(x);
	case MAP_BACKWARD:
		return log(sign * (x - off));
	case MAP_DFORWARD:
		return sign * exp(x);
	case MAP_INCREASING:
		return sign;
	default:
		abort();
	}
	return NAN;
}
double link_logitoffset(double x, map_arg_tp typ, void *param, double *cov)
{
	/*
	 * the link-functions calls the inverse map-function 
	 */
	Link_param_tp *p;
	double prob;

	p = (Link_param_tp *) param;
	prob = map_probability(p->prob_intern[GMRFLib_thread_id][0], MAP_FORWARD, NULL);

	switch (typ) {
	case MAP_FORWARD:
		return prob + (1.0 - prob) * map_probability(x, MAP_FORWARD, NULL);
	case MAP_BACKWARD:
		return map_probability((x - prob) / (1.0 - prob), MAP_BACKWARD, NULL);
	case MAP_DFORWARD:
		return (1.0 - prob) * map_probability(x, MAP_DFORWARD, NULL);
	case MAP_INCREASING:
		return 1.0;
	default:
		abort();
	}
	return NAN;
}
double link_sslogit(double x, map_arg_tp typ, void *param, double *cov)
{
	Link_param_tp *p;
	double sens, spec, a, b;

	p = (Link_param_tp *) param;
	sens = map_probability(p->sensitivity_intern[GMRFLib_thread_id][0], MAP_FORWARD, NULL);
	spec = map_probability(p->specificity_intern[GMRFLib_thread_id][0], MAP_FORWARD, NULL);

	// sens * pi + (1-spec)*(1-pi) = a * pi + b
	a = sens + spec - 1.0;
	b = 1.0 - spec;

	switch (typ) {
	case MAP_FORWARD:
		return a / (1.0 + exp(-x)) + b;

	case MAP_BACKWARD:
		assert((x - b) / (x - b - a) > 0.0);
		return log((x - b) / (x - b - a));

	case MAP_DFORWARD:
		return a * exp(-x) / SQR(1.0 + exp(-x));

	case MAP_INCREASING:
		return (a > 0 ? 1 : 0);
	default:
		abort();
	}
	return NAN;
}
double link_special2(double x, map_arg_tp typ, void *param, double *cov)
{
	/*
	 * the link-functions calls the inverse map-function 
	 */
	Link_param_tp *p;
	double beta, f;

	if (!cov) {
		char *msg;
		GMRFLib_sprintf(&msg, "You need to pass the covariate to the link.model[special2] in the inla()-argument 'link.covariates'");
		inla_error_general(msg);
		exit(1);
	}
	if (cov[0] <= 0.0 || cov[0] >= 1.0) {
		char *msg;
		GMRFLib_sprintf(&msg, "The covariate to link.model[special2] must be between 0 and 1. Your is [%g].", cov[0]);
		inla_error_general(msg);
		exit(1);
	}

	p = (Link_param_tp *) param;
	beta = p->beta[GMRFLib_thread_id][0];
	f = (1.0 - cov[0] + cov[0] * exp(beta));

	switch (typ) {
	case MAP_FORWARD:
		return exp(x) * f;
	case MAP_BACKWARD:
		return log(x / f);
	case MAP_DFORWARD:
		return exp(x) * f;
	case MAP_INCREASING:
		return 1.0;
	default:
		abort();
	}
	return NAN;

}
double link_qpoisson(double x, map_arg_tp typ, void *param, double *cov)
{
	double shape, ret;
	Link_param_tp *lparam = (Link_param_tp *) param;

	switch (typ) {
	case INVLINK:
	{
		shape = exp(x) + 1.0;
		if (shape > 400.0) {
			ret = SQR(sqrt(shape) + sqrt(0.25) * gsl_cdf_ugaussian_Qinv(lparam->quantile));
		} else {
			ret = gsl_cdf_gamma_Qinv(lparam->quantile, shape, 1.0);
		}
	}
		break;

	case LINK:
	{
		CODE_NEEDED;
	}
		break;

	case DINVLINK:
	{
		double dx = GMRFLib_eps(1.0 / 3.9134);	       // about 0.0001 on my laptop
		double wf[] = { 1.0 / 12.0, -2.0 / 3.0, 0.0, 2.0 / 3.0, -1.0 / 12.0 };
		double wf_sum = 0.0;
		int i, nwf = sizeof(wf) / sizeof(double), nwf2 = (nwf - 1) / 2;	/* gives 5 and 2 */

		for (i = 0; i < nwf; i++) {
			wf_sum += wf[i] * link_qpoisson(x + (i - nwf2) * dx, INVLINK, param, cov);
		}
		ret = wf_sum / dx;
	}
		break;

	case LINKINCREASING:
	{
		return 1.0;
	}
		break;

	default:
	{
		assert(0 == 1);
	}
		break;

	}

	return (ret);
}
double link_qweibull(double x, map_arg_tp typ, void *param, double *cov)
{
	Link_param_tp *lparam = (Link_param_tp *) param;
	double alpha = map_alpha_weibull(lparam->alpha_intern[GMRFLib_thread_id][0], MAP_FORWARD, NULL);
	double ret;

	switch (typ) {
	case INVLINK:
	{
		switch (lparam->variant) {
		case 0:
			ret = -1.0 / pow(exp(x), alpha) * log(1.0 - lparam->quantile);
			break;
		case 1:
			ret = 1.0 / exp(x) * pow(-log(1.0 - lparam->quantile), 1.0 / alpha);
			break;
		default:
			assert(0 == 1);
		}
	}
		break;

	case LINK:
	{
		CODE_NEEDED;
	}
		break;

	case DINVLINK:
	{
		double dx = GMRFLib_eps(1.0 / 3.9134);	       // about 0.0001 on my laptop
		double wf[] = { 1.0 / 12.0, -2.0 / 3.0, 0.0, 2.0 / 3.0, -1.0 / 12.0 };
		double wf_sum = 0.0;
		int i, nwf = sizeof(wf) / sizeof(double), nwf2 = (nwf - 1) / 2;	/* gives 5 and 2 */

		for (i = 0; i < nwf; i++) {
			wf_sum += wf[i] * link_qweibull(x + (i - nwf2) * dx, INVLINK, param, cov);
		}
		ret = wf_sum / dx;
	}
		break;

	case LINKINCREASING:
	{
		int ret_val = 0;
		static int do_check[2] = { 1, 1 };

		if (do_check[lparam->variant]) {
#pragma omp critical
			if (do_check[lparam->variant]) {
				if (ret_val != (link_qweibull(x + 1.0, INVLINK, param, cov) > link_qweibull(x, INVLINK, param, cov) ? 1 : 0)) {
					FIXME("LINKINCREASING has error in link_qweibull");
					exit(EXIT_FAILURE);
				}
				do_check[lparam->variant] = 0;
			}
		}
		return (ret_val);
	}
		break;

	default:
	{
		assert(0 == 1);
	}
		break;

	}

	return (ret);
}
