				GMRFLib_sprintf(&ctmp, "theta%1d for POM", count + 1);
				mb->theta_tag[mb->ntheta] = inla_make_tag(ctmp, mb->ds);
				GMRFLib_sprintf(&ctmp, "theta%1d for POM", count + 1);
