			iniparser_getstring(ini, inla_string_join(secname, ctmp), NULL);

			GMRFLib_sprintf(&ctmp, "PARAMETERS%1d", i);
			iniparser_getstring(ini, inla_string_join(secname, ctmp), NULL);

			GMRFLib_sprintf(&ctmp, "to.theta%1d", i);
			iniparser_getstring(ini, inla_string_join(secname, ctmp), NULL);

